mapper.select:
select

from t_banner
where del_flag = 0

  and id = #{id,jdbcType=BIGINT}


  and image_name = #{imageName,jdbcType=VARCHAR}


  and sort = #{sort,jdbcType=INTEGER}


  and jump_to = #{jumpTo,jdbcType=VARCHAR}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and create_time >= #{createTimeBegin,jdbcType=TIMESTAMP}


  and create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}


  and update_time = #{updateTime,jdbcType=TIMESTAMP}


  and update_time >= #{updateTimeBegin,jdbcType=TIMESTAMP}


  and update_time <= #{updateTimeEnd,jdbcType=TIMESTAMP}


  and `desc` = #{desc,jdbcType=VARCHAR}


  and `image_real_name` = #{imageRealName,jdbcType=VARCHAR}


  and `b_name` like concat(concat("%",#{bName}),"%")

order by sort

select count(1) from t_banner
where del_flag = 0

  and id = #{id,jdbcType=BIGINT}


  and image_name = #{imageName,jdbcType=VARCHAR}


  and sort = #{sort,jdbcType=INTEGER}


  and jump_to = #{jumpTo,jdbcType=VARCHAR}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and create_time >= #{createTimeBegin,jdbcType=TIMESTAMP}


  and create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}


  and update_time = #{updateTime,jdbcType=TIMESTAMP}


  and update_time >= #{updateTimeBegin,jdbcType=TIMESTAMP}


  and update_time <= #{updateTimeEnd,jdbcType=TIMESTAMP}


  and `desc` = #{desc,jdbcType=VARCHAR}


  and `image_real_name` = #{imageRealName,jdbcType=VARCHAR}


  and `b_name` like concat(concat("%",#{bName}),"%")
