select

from t_employee
where username = #{username,jdbcType=VARCHAR}
and del_flag = 0;

select

from t_employee
where del_flag = 0

  and id = #{id,jdbcType=BIGINT}


  and e_name = #{eName,jdbcType=VARCHAR}


  and e_name like concat(concat("%",#{searchName}),"%") or username like concat(concat("%",#{searchName}),"%")


  and gender = #{gender,jdbcType=CHAR}


  and birth = #{birth,jdbcType=DATE}


  and birth >= #{birthBegin,jdbcType=DATE}


  and birth <= #{birthEnd,jdbcType=DATE}


  and age = #{age,jdbcType=INTEGER}


  and avatar_real_name = #{avatarRealName,jdbcType=VARCHAR}


  and create_time = #{createTime,jdbcType=DATE}


  and create_time >= #{createTimeBegin,jdbcType=DATE}


  and create_time <= #{createTimeEnd,jdbcType=DATE}


  and update_time = #{updateTime,jdbcType=DATE}


  and update_time >= #{updateTimeBegin,jdbcType=DATE}


  and update_time <= #{updateTimeEnd,jdbcType=DATE}


  and delFlag = #{delFlag,jdbcType=INTEGER}


  and password = #{password,jdbcType=VARCHAR}


  and username = #{username,jdbcType=VARCHAR}


  and email = #{email,jdbcType=VARCHAR}


  and mobile = #{mobile,jdbcType=VARCHAR}


  and wechat = #{wechat,jdbcType=VARCHAR}


  and avatar_name = #{avatarName,jdbcType=VARCHAR}

