mapper.select:
select

from t_order where f_del_flag = 0

  and f_order_no = #{orderNo,jdbcType=VARCHAR}


  and f_order_status = #{orderStatus,jdbcType=VARCHAR}


  and f_goods_id = #{goodsId,jdbcType=BIGINT}


  and f_goods_type = #{goodsType,jdbcType=VARCHAR}


  and f_goods_price = #{goodsPrice,jdbcType=DECIMAL}


  and f_user_id = #{userId,jdbcType=BIGINT}


  and f_skey = #{skey,jdbcType=VARCHAR}


  and f_total_amount = #{totalAmount,jdbcType=DECIMAL}


  and f_points = #{points,jdbcType=DECIMAL}


  and f_wx_amount = #{wxAmount,jdbcType=DECIMAL}


  and f_referrer = #{referrer,jdbcType=BIGINT}


      and f_invitation_id = #{invitationId,jdbcType=BIGINT}


      and f_created_time = #{createdTime,jdbcType=DATE}


      and f_created_time >= #{createdTimeBegin,jdbcType=DATE}


      and f_created_time <= #{createdTimeEnd,jdbcType=DATE}


      and f_modified_time = #{modifiedTime,jdbcType=DATE}


      and f_modified_time >= #{modifiedTimeBegin,jdbcType=DATE}


      and f_modified_time <= #{modifiedTimeEnd,jdbcType=DATE}

select count(1)
from t_order where del_flag = 0

  and order_no = #{orderNo,jdbcType=VARCHAR}


  and f_order_status = #{orderStatus,jdbcType=VARCHAR}


  and f_goods_id = #{goodsId,jdbcType=BIGINT}


  and f_goods_type = #{goodsType,jdbcType=VARCHAR}


  and f_goods_price = #{goodsPrice,jdbcType=DECIMAL}


  and f_user_id = #{userId,jdbcType=BIGINT}


  and f_skey = #{skey,jdbcType=VARCHAR}


  and f_total_amount = #{totalAmount,jdbcType=DECIMAL}


  and f_points = #{points,jdbcType=DECIMAL}


  and f_wx_amount = #{wxAmount,jdbcType=DECIMAL}


  and f_referrer = #{referrer,jdbcType=BIGINT}


      and f_invitation_id = #{invitationId,jdbcType=BIGINT}


      and f_created_time = #{createdTime,jdbcType=DATE}


      and f_created_time >= #{createdTimeBegin,jdbcType=DATE}


      and f_created_time <= #{createdTimeEnd,jdbcType=DATE}


      and f_modified_time = #{modifiedTime,jdbcType=DATE}


      and f_modified_time >= #{modifiedTimeBegin,jdbcType=DATE}


      and f_modified_time <= #{modifiedTimeEnd,jdbcType=DATE}
