mapper.select:
select

from t_order where del_flag = 0

  and order_no = #{orderNo,jdbcType=VARCHAR}


  and order_status = #{orderStatus,jdbcType=VARCHAR}


  and goods_id = #{goodsId,jdbcType=BIGINT}


  and goods_type = #{goodsType,jdbcType=VARCHAR}


  and goods_price = #{goodsPrice,jdbcType=DECIMAL}


  and user_id = #{userId,jdbcType=BIGINT}


  and total_amount = #{totalAmount,jdbcType=DECIMAL}


  and account_balance_amount = #{accountBalanceAmount,jdbcType=DECIMAL}


  and wx_amount = #{wxAmount,jdbcType=DECIMAL}


  and referrer = #{referrer,jdbcType=BIGINT}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and create_time >= #{createTimeBegin,jdbcType=TIMESTAMP}


  and create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}


  and update_time = #{updateTime,jdbcType=TIMESTAMP}


  and update_time >= #{updateTimeBegin,jdbcType=TIMESTAMP}


  and update_time <= #{updateTimeEnd,jdbcType=TIMESTAMP}

select count(1)
from t_order where del_flag = 0

  and order_no = #{orderNo,jdbcType=VARCHAR}


  and order_status = #{orderStatus,jdbcType=VARCHAR}


  and goods_id = #{goodsId,jdbcType=BIGINT}


  and goods_type = #{goodsType,jdbcType=VARCHAR}


  and goods_price = #{goodsPrice,jdbcType=DECIMAL}


  and user_id = #{userId,jdbcType=BIGINT}


  and total_amount = #{totalAmount,jdbcType=DECIMAL}


  and account_balance_amount = #{accountBalanceAmount,jdbcType=DECIMAL}


  and wx_amount = #{wxAmount,jdbcType=DECIMAL}


  and referrer = #{referrer,jdbcType=BIGINT}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and create_time >= #{createTimeBegin,jdbcType=TIMESTAMP}


  and create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}


  and update_time = #{updateTime,jdbcType=TIMESTAMP}


  and update_time >= #{updateTimeBegin,jdbcType=TIMESTAMP}


  and update_time <= #{updateTimeEnd,jdbcType=TIMESTAMP}
