mapper.insert:
insert into t_share_card (id, user_id, skey, 
  share_card_real_name, goods_id, goods_type, 
  create_time, update_time, del_flag
  )
values (#{id,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, #{skey,jdbcType=VARCHAR}, 
  #{shareCardRealName,jdbcType=VARCHAR}, #{goodsId,jdbcType=BIGINT}, #{goodsType,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{delFlag,jdbcType=INTEGER}
  )

mapper.select:
select

from t_share_card
where
del_flag = 0
and user_id = #{userId, jdbcType=BIGINT}
and goods_id = #{goodsId, jdbcType=BIGINT}
and goods_type = #{goodsType, jdbcType=VARCHAR}
