mapper.select:
select
v.id, v.title, v.subtitle, v.price, v.cover_real_name, v.description_real_name, v.video_real_name,
v.create_time, v.update_time, v.del_flag, v.classification, v.profit_share, v.status, v.cover_name,
v.description_name, v.video_name, v.free_watch_time,
vc.v_name as classification_name,
(select count(1) from t_statistics s where s.statistics_type = '003003' and s.business_id = v.id) as watched,
(select count(1) from t_statistics s where s.statistics_type = '003004' and s.business_id = v.id) as purchased,
(select count(1) from t_statistics s where s.statistics_type = '003005' and s.business_id = v.id) as accessed
from t_video v
left join t_video_classification vc on v.classification = vc.id
where v.del_flag = 0

  and v.title like concat(concat("%",#{title}),"%")


  and v.subtitle = #{subtitle,jdbcType=VARCHAR}


  and v.price = #{price,jdbcType=DECIMAL}


  and v.cover_real_name = #{coverRealName,jdbcType=VARCHAR}


  and v.description_real_name = #{descriptionRealName,jdbcType=VARCHAR}


  and v.video_real_name = #{videoRealName,jdbcType=VARCHAR}


  and v.create_time = #{createTime,jdbcType=TIMESTAMP}


  and v.create_time >= #{createTimeBegin,jdbcType=TIMESTAMP}


  and v.create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}


  and v.update_time = #{updateTime,jdbcType=TIMESTAMP}


  and v.update_time >= #{updateTimeBegin,jdbcType=TIMESTAMP}


  and v.update_time <= #{updateTimeEnd,jdbcType=TIMESTAMP}


  and v.del_flag = #{delFlag,jdbcType=INTEGER}


  and v.classification = #{classification,jdbcType=BIGINT}


  and v.profit_share = #{profitShare,jdbcType=DECIMAL}


  and v.status = #{status,jdbcType=VARCHAR}


  and v.cover_name = #{coverName,jdbcType=VARCHAR}


  and v.description_name = #{descriptionName,jdbcType=VARCHAR}


  and v.video_name = #{videoName,jdbcType=VARCHAR}


  and v.free_watch_time = #{freeWatchTime,jdbcType=INTEGER}


  order by v.${orderBy} ${sequence}

select count(1) from t_video
where del_flag = 0

  and title like concat(concat("%",#{title}),"%")


  and subtitle = #{subtitle,jdbcType=VARCHAR}


  and price = #{price,jdbcType=DECIMAL}


  and cover_real_name = #{coverRealName,jdbcType=VARCHAR}


  and description_real_name = #{descriptionRealName,jdbcType=VARCHAR}


  and video_real_name = #{videoRealName,jdbcType=VARCHAR}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and create_time >= #{createTimeBegin,jdbcType=TIMESTAMP}


  and create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}


  and update_time = #{updateTime,jdbcType=TIMESTAMP}


  and update_time >= #{updateTimeBegin,jdbcType=TIMESTAMP}


  and update_time <= #{updateTimeEnd,jdbcType=TIMESTAMP}


  and del_flag = #{delFlag,jdbcType=INTEGER}


  and classification = #{classification,jdbcType=BIGINT}


  and profit_share = #{profitShare,jdbcType=DECIMAL}


  and status = #{status,jdbcType=VARCHAR}


  and cover_name = #{coverName,jdbcType=VARCHAR}


  and description_name = #{descriptionName,jdbcType=VARCHAR}


  and video_name = #{videoName,jdbcType=VARCHAR}


  and free_watch_time = #{freeWatchTime,jdbcType=INTEGER}

select v.id, v.title, v.subtitle, v.price, v.cover_real_name, v.description_real_name, v.video_real_name, v.create_time, v.update_time, v.del_flag, v.classification, v.profit_share, v.status, v.cover_name, v.description_name, v.video_name, v.free_watch_time, vc.v_name as classification_name, (select count(1) from t_statistics s where s.statistics_type = '003003' and s.business_id = v.id) as watched, (select count(1) from t_statistics s where s.statistics_type = '003004' and s.business_id = v.id) as purchased, (select count(1) from t_statistics s where s.statistics_type = '003005' and s.business_id = v.id) as accessed
,(case when o.id is null then false else true end) as is_purchased
,false as is_purchased
from t_video v left join t_video_classification vc on v.classification = vc.id
left join t_order o on v.id = o.goods_id and o.user_id = #{userId,jdbcType=BIGINT}
where v.id = #{videoId,jdbcType=BIGINT}
