mapper.select:
select
v.id, v.title, v.subtitle, v.price, v.watched, v.purchased, v.cover_real_name, v.description_real_name, v.video_real_name,
v.create_time, v.update_time, v.del_flag, v.classification, v.share_commission, v.accessed, v.status, v.cover_name,
v.description_name, v.video_name, vc.v_name as classification_name
from t_video v
left join t_video_classification vc on v.classification = vc.id
where v.del_flag = 0

  and v.title like concat(concat("%",#{title}),"%")


  and v.subtitle = #{subtitle,jdbcType=VARCHAR}


  and v.price = #{price,jdbcType=DECIMAL}


  and v.watched = #{watched,jdbcType=INTEGER}


  and v.purchased = #{purchased,jdbcType=INTEGER}


  and v.cover_real_name = #{coverRealName,jdbcType=VARCHAR}


  and v.description_real_name = #{descriptionRealName,jdbcType=VARCHAR}


  and v.video_real_name = #{videoRealName,jdbcType=VARCHAR}


  and v.create_time = #{createTime,jdbcType=TIMESTAMP}


  and v.create_time >= #{createTimeBegin,jdbcType=TIMESTAMP}


  and v.create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}


  and v.update_time = #{updateTime,jdbcType=TIMESTAMP}


  and v.update_time >= #{updateTimeBegin,jdbcType=TIMESTAMP}


  and v.update_time <= #{updateTimeEnd,jdbcType=TIMESTAMP}


  and v.del_flag = #{delFlag,jdbcType=INTEGER}


  and v.classification = #{classification,jdbcType=BIGINT}


  and v.share_commission = #{shareCommission,jdbcType=DECIMAL}


  and v.accessed = #{accessed,jdbcType=INTEGER}


  and v.status = #{status,jdbcType=VARCHAR}


  and v.cover_name = #{coverName,jdbcType=VARCHAR}


  and v.description_name = #{descriptionName,jdbcType=VARCHAR}


  and v.video_name = #{videoName,jdbcType=VARCHAR}

select count(1) from t_video
where del_flag = 0

  and title like concat(concat("%",#{title}),"%")


  and subtitle = #{subtitle,jdbcType=VARCHAR}


  and price = #{price,jdbcType=DECIMAL}


  and watched = #{watched,jdbcType=INTEGER}


  and purchased = #{purchased,jdbcType=INTEGER}


  and v.cover_real_name = #{coverRealName,jdbcType=VARCHAR}


  and v.description_real_name = #{descriptionRealName,jdbcType=VARCHAR}


  and v.video_real_name = #{videoRealName,jdbcType=VARCHAR}


  and create_time = #{createTime,jdbcType=TIMESTAMP}


  and create_time >= #{createTimeBegin,jdbcType=TIMESTAMP}


  and create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}


  and update_time = #{updateTime,jdbcType=TIMESTAMP}


  and update_time >= #{updateTimeBegin,jdbcType=TIMESTAMP}


  and update_time <= #{updateTimeEnd,jdbcType=TIMESTAMP}


  and del_flag = #{delFlag,jdbcType=INTEGER}


  and classification = #{classification,jdbcType=BIGINT}


  and share_commission = #{shareCommission,jdbcType=DECIMAL}


  and accessed = #{accessed,jdbcType=INTEGER}


  and v.status = #{status,jdbcType=VARCHAR}


  and v.cover_name = #{coverName,jdbcType=VARCHAR}


  and v.description_name = #{descriptionName,jdbcType=VARCHAR}


  and v.video_name = #{videoName,jdbcType=VARCHAR}
